var path = require('path');
var fs = require("fs");

exports.IDEAngularModules = function() {
    var libs = [
        'ide/js/twx-ide-resource.js'
    ];
    return fullyQualify(libs);
};

exports.runtimeLibraries = function() {
    let localJsSDK = fs.existsSync(path.resolve(__dirname, 'node_modules/tw-javascript-sdk'));
    let basePath = 'node_modules/';
    let jsSDKPath = 'tw-javascript-sdk/dist/tw-javascript-sdk-0.2.3-min.js';
    if(!localJsSDK) {
        basePath = '../';
    }
    var libs = [
        'runtime/js/twx-runtime-resource.js',
        basePath + jsSDKPath
    ];
    return fullyQualify(libs);
};

function fullyQualify(libs) {
    var i = libs.length;
    while (i--) {
        libs[i] = path.resolve(__dirname, libs[i]);
    }
    return libs;
}