'use strict';

var path = require('path');

function oncreate(params) {
  return new Promise(function(resolve, reject) {
    try {
      const logger = params.logger;
      logger.info('Creating ves-ar-extension app:', params.name);
      var pathDir = params.path;
      var gulpShared = require(pathDir + '/gulp-shared.js');
      const scaffoldDir = gulpShared.getScaffoldDir(params);
      gulpShared.init(path.join(scaffoldDir, 'templates'), params);
      resolve();
    }
    catch (e) {
      reject(e);
      console.error("Error Initializing", e);
    }
  });
}



module.exports = {
  oncreate: oncreate
};
