function twxTMLText() {
  return {
    elementTag: 'twx-tml-text',

    label: 'ves-ar-extension:TML Text',

    category: 'ar',

    properties: [
      {
        name: 'text',
        label: 'ves-ar-extension:Text',
        datatype: 'xml',
        default: '',
        isVisible: false
      },
      {
        name: 'textEditor',
        label: 'ves-ar-extension:Text',
        datatype: 'custom_ui',
        buttonLabel: 'Edit Text',
        title: 'Edit Text',
        template: function () {
          return '<textarea ng-model="props.text.value" ng-model-options="{ updateOn: \'default blur\', debounce: { \'default\': 300, \'blur\': 0 } }"  rows="10" cols="80"></textarea>';
        }
      }
    ],

    services: [],

    designTemplate: function () {
      return '';
    },

    runtimeTemplate: function (props) {
      var tmpl = props.text;
      return tmpl;
    }
  };
}

twxAppBuilder.widget('twxTmlText', twxTMLText);