function twxFixedLabel() {
  return {
    elementTag: 'twx-fixed-label',

    label: 'ves-ar-extension:Fixed Label',

    category: 'ionic-2D',

    isVisibleInPalette: false,

    properties: [
      {
        name: 'text',
        label: 'ves-ar-extension:Text',
        datatype: 'string',
        default: 'Label',
        isBindingTarget: true
      },
      {
        name: 'class',
        label: 'ves-ar-extension:Class',
        datatype: 'string',
        default: 'simple-label'
      },
      {
        name: 'top',
        label: 'ves-ar-extension:Top',
        datatype: 'string'
      },
      {
        name: 'left',
        label: 'ves-ar-extension:Left',
        datatype: 'string'
      },
      {
        name: 'visible',
        label: 'ves-ar-extension:Visible',
        datatype: 'boolean',
        default: true,
        isBindingTarget: true
      }
    ],

    designTemplate: function () {
      return '<span class="{{me.class}}" style="top:{{me.top}}px; left:{{me.left}}px;">{{me.text}}</span>';
    },

    runtimeTemplate: function (props) {
      var tmpl = '<div ng-show="app.fn.isTrue(me.visible)" class="ng-hide labelWidget" style="top: ' + props.top + 'px; left: ' + props.left + 'px;"><span class="{{me.class}}">{{me.text}}</span></div>';
      return tmpl;
    }
  };
}

twxAppBuilder.widget('twxFixedLabel', twxFixedLabel);