function twxSpinner() {
    return {
        elementTag: 'twx-spinner',

        label: 'ves-ar-extension:Spinner',

        category: 'ionic-2D',

        properties: [
            {
                name: 'icon',
                label: 'ves-ar-extension:Spinner Type',
                datatype: 'string',
                default: 'spiral',
                options: [
                    { label: 'ves-ar-extension:Spiral', value: 'spiral' },
                    { label: 'ves-ar-extension:Ripple', value: 'ripple' }
                ]
            },
            {
                name: 'visible',
                label: 'ves-ar-extension:Visible',
                datatype: 'boolean',
                default: true,
                isBindingTarget: true
            },
            {
              name: 'class',
              label: 'ves-ar-extension:Class',
              datatype: 'string',
              default: 'spinner-ios',
              isBindingTarget: true
            }
        ],

        services: [],

        events: [
        ],

        designTemplate: function () {
            return '<img class="{{me.class}}" src="/images/twxSpinner.png">';
        },

        runtimeTemplate: function (props) {
            var tmpl = '<ion-spinner class="spinner {{me.class}}" icon="{{me.icon}}" ng-show="app.fn.isTrue(me.visible)"></ion-spinner>';
            return tmpl;
        }
    };
}

twxAppBuilder.widget('twxSpinner', twxSpinner);