(function(twxAppBuilder){

  function twxViewFooter(){
    return {
      elementTag: 'twx-view-footer',

      label: 'ves-ar-extension:Footer',

      category: 'ionic-2D',

      isContainer: true,

      isVisibleInPalette: true,

      properties: [
        {
            name: 'class',
            label: 'ves-ar-extension:Class',
            datatype: 'string'
        }
      ],

      designTemplate: function(){
        return '<div class="footer bar bar-footer {{me.class}}"><twx-container-content></twx-container-content></div>';
      },

      runtimeTemplate: function(props){
        var tmpl = '<ion-footer-bar class="bar bar-footer '+props.class+'"><twx-container-content></twx-container-content></ion-footer-bar>';
        return tmpl;
      }
    };
  }

  twxAppBuilder.widget('twxViewFooter', twxViewFooter);

})(twxAppBuilder);