var fs = require('fs-extra');
var strip = require('strip-comments');

var translations = fs.readFileSync('./locales/en/ves-basic-web-widgets-extension.json', {encoding: 'utf8'});
translations = strip(translations, {preserveNewlines: false});
translations = JSON.parse(translations);

var jaJP = {};

Object.keys(translations).forEach(function(key) {
  jaJP[key] = "'" + translations[key] + "ステー'";
});
fs.ensureDirSync('./locales/ja_JP/');
fs.writeJson('./locales/ja_JP/ves-basic-web-widgets-extension.json', jaJP);