(function (twxAppBuilder) {

  function twxTabs() {
    return {
      elementTag: 'twx-tabs',

      label: 'ves-basic-web-widgets-extension:Tabs',

      category: 'basic-html',

      isContainer: true,

      properties: [
        {
          name: 'visible',
          label: 'ves-basic-web-widgets-extension:Visible',
          datatype: 'boolean',
          default: true,
          isBindingTarget: true
        },
        {
          name: 'class',
          label: 'ves-basic-web-widgets-extension:Class',
          datatype: 'string',
          //default: '',
          isBindingTarget: true
        },
        {
          name: 'taborientation',
          label: 'ves-basic-web-widgets-extension:Tab Orientation',
          datatype: 'string',
          default: 'horizontal',
          editor: 'select',
          options: [
            {label: 'ves-basic-web-widgets-extension:Horizontal', value: 'horizontal'},
            {label: 'ves-basic-web-widgets-extension:Vertical', value: 'vertical'}
          ],
          alwaysWriteAttribute: true
        },
        {
          name: 'tabpadding',
          label: 'ves-basic-web-widgets-extension:Tab Padding',
          datatype: 'string',
          default: '0px'
        },
        {
          name: 'stripclass',
          label: 'ves-basic-web-widgets-extension:Tab Strip Class',
          datatype: 'string',
          default: ''
        },
        {
          name: 'margin',
          label: 'ves-basic-web-widgets-extension:Margin',
          datatype: 'string',
          default: ''
        }
      ],

      events: [
        {
          name: 'clicktab',
          label: 'ves-basic-web-widgets-extension:Tab Click'
        }
      ],

      initialContent: function(){
        return '<twx-tab twx-widget><twx-container-content></twx-container-content></twx-tab>';
      },

      designTemplate: function () {
        return '<twx-tabs-designer class="{{me.taborientation}} {{me.class}}" stripclass="{{me.stripclass}}" margin="{{me.margin}}"><twx-container-content></twx-container-content></twx-tabs-designer>';
      },

      runtimeTemplate: function (properties) {
        var tmpl = '<twx-tabs stripclass="'+ properties.stripclass +'" clicktab="fireEvent(\'clicktab\');" ng-show="app.fn.isTrue(me.visible)" class="twx-tabs {{me.class}} '+ properties.taborientation +'" style="padding:'+ properties.tabpadding +'; margin: '+ properties.margin +';"><twx-container-content></twx-container-content></twx-tabs>';
        return tmpl;
      }
    }
  }

  twxAppBuilder.widget('twxTabs', twxTabs);

})(twxAppBuilder);