var fs = require('fs');
var path = require('path');

function getProjects(srcPath) {
  // return the sample project dirs
  return fs.readdirSync(srcPath).filter(function(file) {
    // each directory should be a project
    return fs.statSync(path.join(srcPath, file)).isDirectory();
  });
};

var sampleProjects = [];

// find all the sample projects within this module and add them to the sampleProjects array
var projectsDir = path.join(__dirname, 'projects');
var projects = getProjects(projectsDir);
Object.keys(projects).forEach( function(index) {
  var projectName = projects[index];
  var srcPath = path.join(projectsDir, projectName);
  sampleProjects.push({name: projectName, path: srcPath});
});

module.exports = sampleProjects;